// Minimal local shim for hls.js
// This placeholder avoids "Hls is not defined" and MIME errors when
// the real hls.js cannot be loaded. It does NOT implement full HLS
// functionality. Replace with the real minified hls.js for full support.
(function(){
    function HlsShim(){
        this.loadSource = function(){};
        this.attachMedia = function(){};
        this.on = function(){};
        this.destroy = function(){};
    }
    HlsShim.isSupported = function(){ return false; };
    HlsShim.Events = { MANIFEST_PARSED: 'manifestParsed', ERROR: 'error' };
    // expose as global Hls for compatibility
    window.Hls = HlsShim;
})();
